//Ejercicio 13 
//Escribe un programa que rellene un array de 100 elementos con números ente- 
//ros aleatorios comprendidos entre 0 y 500 (ambos incluidos). A continuación 
//el programa mostrará el array y preguntará si el usuario quiere destacar el 
//máximo o el mínimo. Seguidamente se volverá a mostrar el array escribiendo 
//el número destacado entre dobles asteriscos.

package prueba;

import java.util.Scanner;

public class ejercicio13Tema7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n[] = new int[100];
		int i;
		int maximo = 500;
		int minimo = 0;

		for (i = 0; i < 100; i++) {
			n[i] = (int) (Math.random() * 501);

			if (n[i] < minimo) {
				minimo = n[i];
			}

			if (n[i] < maximo) {
				maximo = n[i];
			}
		}
		for (int elemento : n) {
			System.out.print(elemento + " ");

		}
		

		Scanner s = new Scanner(System.in);

		System.out.print("\n\n¿Qué quiere destacar? (1 – mínimo, 2 – máximo): ");
		int opcion = s.nextInt();

		int destacado;
		if (opcion == 1) {
			destacado = minimo;

		} else {
			destacado = maximo;
		}

		System.out.println();

		for (int elemento : n) {
			if (elemento == destacado) {
				System.out.print(" **" + elemento + "** ");
			} else {
				System.out.print(elemento + " ");
			}
		}
	}

}